pragma solidity ^0.4.24;

contract FarmContract
{
    address private owner;
    mapping(bytes => address) private products;
    mapping(bytes => address) private medias;

    constructor() public
    {
        owner = msg.sender;
    }

    modifier onlymanager()
    {
        require(msg.sender == owner);
        _;
    }

    function createProduct(bytes _name) public onlymanager
    {

        address product = new Product(_name);

        products[_name] = product;

    }
    function addMedia(bytes _filename,
                      bytes _timestamp,
                      bytes _longitude,
                      bytes _latitude) public onlymanager
    {

        address media = new Media(_filename, _timestamp, _longitude, _latitude);

        medias[_filename] = media;


    }

    function addMediaToProduct(bytes _media,
                               bytes _product) public onlymanager
    {

        address product_addr = products[_product];

        require(product_addr != 0x0, "Prodotto non trovato");

        Product product = Product(product_addr);
        address media_addr = medias[_media];
        product.addMedia(_media, media_addr);
    }
}


contract Product
{
    bytes public name;
    mapping(bytes => address) private medias;

    address private owner;

    modifier onlymanager()
    {
        require(msg.sender == owner);
        _;
    }

    constructor(bytes _name) public
    {
        // This will only be managed by the "father" contract ("CarrefourFactory"):
        owner = msg.sender;

        name = _name;
    }
    function addMedia(bytes _media, address _media_addr) public onlymanager
    {
        medias[_media] = _media_addr;
    }
    

}

contract Media
{
    bytes   public filename;
    bytes public timestamp;
    bytes public longitude;
    bytes   public latitude;

    constructor(bytes   _filename,
                bytes _timestamp,
                bytes _longitude,
                bytes   _latitude) public
    {
        filename = _filename;
        timestamp = _timestamp;
        longitude = _longitude;
        latitude = _latitude;
    }
}
